(() => {
  "use strict";
  window.addEventListener("load", function () {
    const e = new Swiper(".elm", {
        wrapperClass: "elm__wrapper",
        slideClass: "elm__screen",
        slidesPerViev: "auto",
        keyboard: { enabled: !0, onlyInViewport: !0, pageUpDown: !0 },
        mousewheel: { sensitivity: 1 },
        wachOverflow: !0,
        speed: 800,
        observer: !0,
        observeParents: !0,
        observeSlideChildren: !0,
        pagination: {
          el: ".page__pagination",
          type: "bullets",
          clickable: !0,
          bulletClass: "page__bullet",
          bulletActiveClass: "page__bullet_active",
        },
      }),
      t = new Swiper(".el__content", {
        wrapperClass: "el__wrapper",
        slideClass: "el__screen",
        slidesPerViev: 1,
        wachOverflow: !0,
        speed: 800,
        observer: !0,
        observeParents: !0,
        observeSlideChildren: !0,
        slideToCliskedSlide: !1,
        simulateTouch: !1,
        touchRatio: 0,
        loop: !1,
        effect: "fade",
        fadeEffect: { crossFade: !0 },
      });
    e.controller.control = t;
    let n = null,
      l = window.screen.availWidth;
    function i() {
      (l = Math.max(
        document.documentElement.clientWidth || 0,
        window.innerWidth || 0
      )),
        l <= 1340
          ? n ||
            (n = new Swiper(".el__slider", {
              wrapperClass: "el__list",
              slideClass: "el__item",
              nested: !0,
              grabCursor: !0,
              loop: !1,
              freeMode: !0,
              slidesPerView: "auto",
              centeredSlides: !1,
              spaceBetween: 0,
              wachOverflow: !0,
              speed: 800,
              observer: !0,
              observeParents: !0,
              observeSlideChildren: !0,
            }))
          : n &&
            (n.forEach((e) => {
              e.destroy();
            }),
            (n = null));
    }
    i(), window.addEventListener("resize", i);
  }),
    (function () {
      function e() {
        let e =
          Math.min(
            document.documentElement.scrollHeight,
            document.documentElement.offsetHeight,
            document.documentElement.clientHeight,
            window.innerHeight
          ) / 100;
        document.documentElement.style.setProperty("--vh", `${e}px`);
      }
      e(), (window.onresize = e);
    })(),
    (function () {
      const e = document.querySelector(".header__content");
      if (e) {
        const t = e.querySelector(".nav__icon-activ"),
          n = e.querySelector(".nav__icon"),
          l = e.querySelectorAll(".nav__link");
        function i() {
          e.classList.toggle("nav-activ");
        }
        function s() {
          e.classList.contains("nav-activ") && i();
        }
        t &&
          n &&
          l.length > 0 &&
          (n.addEventListener("click", i),
          t.addEventListener("click", i),
          l.forEach((e) => {
            e.addEventListener("click", s);
          }));
      }
    })();
})();
